
public class LoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=1;
		System.out.println("Number Is");
		while(num<=10) {
			System.out.println(num);
			num = num+ 1;
		}
		System.out.println("Do while Demo");
		int numb=1;
		do {
			System.out.println(numb);
			numb = numb + 1;
		}while(numb<=10);
		
	}

}
